import React, { Fragment, useEffect, useState } from 'react'
import { Table, Row, Col, Button } from 'antd';
import Layout, { Header, Content, Footer } from 'antd/lib/layout/layout'
import axios from 'axios';
import { Link } from 'react-router-dom/cjs/react-router-dom.min';
// const { Column, ColumnGroup } = Table;


const columns =[
  {
      title:'Name',
      dataIndex:'name',
      key:'name'

  } ,

  {
      title:'email',
      dataIndex:'email',
      key:'name'
  }

]

function Read() {
  const [apiData, setApiData] = useState([]);
    useEffect(() => {
        axios.get(`https://61bad66ee943920017784a53.mockapi.io/crud`)
            .then((getData) => {
                setApiData(getData.data);
            })
    }, [])

    const setData = (id, firstName, lastName) => {
        localStorage.setItem('ID', id)
        localStorage.setItem('name', firstName)
        localStorage.setItem('lastName', lastName)
    }

    const getData = () => {
        axios.get(`https://60cdfb0691cc8e00178dc448.mockapi.io/Crud`)
            .then((getData) => {
                setApiData(getData.data);
            })
    }

    const onDelete = (id) => {
        axios.delete(`https://60cdfb0691cc8e00178dc448.mockapi.io/Crud/${id}`)
        .then(() => {
            getData();
        })
    }





    return (
        <Fragment>
               <Table celled>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>ID</Table.HeaderCell>
                        <Table.HeaderCell>First Name</Table.HeaderCell>
                        <Table.HeaderCell>Last Name</Table.HeaderCell>
                        <Table.HeaderCell>Update</Table.HeaderCell>
                        <Table.HeaderCell>Delete</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>

                <Table.Body>
                    {apiData.map((data) => {
                        return (
                            <Table.Row>
                                <Table.Cell>{data.id}</Table.Cell>
                                <Table.Cell>{data.firstName}</Table.Cell>
                                <Table.Cell>{data.lastName}</Table.Cell>
                                <Table.Cell>
                                    <Link to='/update'>
                                        <Button
                                            color="green"
                                            onClick={() => setData(data.id, data.firstName, data.lastName)}>
                                            Update
                                        </Button>
                                    </Link>
                                </Table.Cell>
                                <Table.Cell>
                                    <Button color="red" onClick={() => onDelete(data.id)}>Delete</Button>
                                </Table.Cell>
                            </Table.Row>
                        )
                    })}

                </Table.Body>
            </Table>
        </Fragment>    
    )
}

export default Read
